mapper.select:
select Id, good_name, manufacturer_id, supplier_id, specifications, purchase_price, member_price, status, create_time, creator, update_time, updater from t_goods_info e WHERE 1 = 1 AND status = 1
AND e.manufacturer_id = #{manufacturerId, jdbcType=INTEGER}
AND e.good_name LIKE #{pattern}

select e.Id, good_name, manufacturer_id, e.supplier_id, specifications, purchase_price, member_price, e.status, create_time, creator, update_time, updater from t_goods_info e, t_supplier a, t_group_map b WHERE 1 = 1 AND e.status = 1 AND a.id = b.supplier_id AND e.supplier_id = a.id AND b.member_id = #{memberId, jdbcType=INTEGER}
AND e.manufacturer_id = #{manufacturerId, jdbcType=INTEGER}
AND e.good_name LIKE #{pattern}

select a.member_id, c.member_name, c.brand, d.param_value as brand_name, a.meituan_profit, a.eleme_profit, a.settle_date, IFNULL(b.order_amount, 0) as order_amount from t_platform_profit a left join (select member_id, order_date, sum(member_price*num) as order_amount from t_order_info group by member_id, order_date) b on a.settle_date = b.order_date and a.member_id = b.member_id inner join t_member_info c on a.member_id = c.id
AND c.brand = #{brandId, jdbcType=VARCHAR}
AND c.id = #{memberId, jdbcType=INTEGER}
AND a.settle_date >= #{beginDate, jdbcType=VARCHAR}
AND a.settle_date <= #{endDate, jdbcType=VARCHAR}
inner join t_sys_param d on d.param_name = 'brand' and d.param_key = c.brand union select a.member_id, c.member_name, c.brand, d.param_value as brand_name, IFNULL(a.meituan_profit, 0) as meituan_profit, IFNULL(a.eleme_profit, 0) as eleme_profit, b.order_date as settle_date, b.order_amount from t_platform_profit a right join (select member_id, order_date, sum(member_price*num) as order_amount from t_order_info group by member_id, order_date) b on a.settle_date = b.order_date and a.member_id = b.member_id inner join t_member_info c on b.member_id = c.id
AND c.brand = #{brandId, jdbcType=VARCHAR}
AND c.id = #{memberId, jdbcType=INTEGER}
AND b.order_date >= #{beginDate, jdbcType=VARCHAR}
AND b.order_date <= #{endDate, jdbcType=VARCHAR}
inner join t_sys_param d on d.param_name = 'brand' and d.param_key = c.brand

select d.brand as brand_id, e.param_value as brand_name, member_id, member_name, good_id, c.good_name, price, num, supplierId, supplier_name, order_date, b.member_price, f.param_value as manufacturer_name, c.manufacturer_id from t_supplier a, ( select member_id, good_id, member_price, purchase_price as price, num, order_date, supplierId as supplierId from t_order_info where 1=1
AND order_date >= #{beginDate, jdbcType=VARCHAR}
AND order_date <= #{endDate, jdbcType=VARCHAR}
AND supplierId = #{supplierId, jdbcType=INTEGER}
union all select member_id, good_id, 0 as member_price, service_price as price, num, order_date, service_id as supplierId from t_order_info b where service_id is not null
AND order_date >= #{beginDate, jdbcType=VARCHAR}
AND order_date <= #{endDate, jdbcType=VARCHAR}
AND service_id = #{supplierId, jdbcType=INTEGER}
) b, t_goods_info c, t_member_info d, t_sys_param e, t_sys_param f where a.id = b.supplierId and c.id = b.good_id and d.id = b.member_id and e.param_name = 'brand' and e.param_key = d.brand and c.manufacturer_id = f.param_key and f.param_name = 'manufacturer'
